  uri.hostname = this.getHostname(uri.hostname, this.edge, this.region);

  return this.httpClient.request({
    method: opts.method,
    uri: uri.href,
    username: username,
    password: password,
    headers: headers,
    params: opts.params,
    data: opts.data,
    timeout: opts.timeout,
    allowRedirects: opts.allowRedirects,
    logLevel: opts.logLevel || this.logLevel
